import Navbar from "../../components/navbar";
import VideoSingleContainer from "../../components/video/video-single-container";
import {useRouter} from "next/router";
import {useEffect, useState} from "react";
import axios from "axios";
interface LargeComment {
    id: number;
    comment: string;
    createdAt: string;
    modifiedAt: string;
    userId: number;
    email:string;
    name: string;
    userImg: string;
}

interface Comment {
    id: number;
    comment: string;
    createdAt: string;
    modifiedAt: string;
    userId: number;
    email:string;
    name: string;
    userImg: string;
    largeComments: LargeComment[];
}
interface Video {
    id: number;
    title: string;
    description: string;
    createdAt: string;
    modifiedAt: string;
    thumbnailUrl: string;
    videoUrl: string;
    viewCount: number
    goodStatus: boolean,
    badStatus: boolean;
    userId: number;
    email:string;
    name: string;
    userImg: string;
    comments : Comment[];
}

export default function Video() {
    const [video, setVideo] =  useState<Video>();
    const router = useRouter()

    useEffect(() => {
        if(router.asPath !== router.route) {
            console.log('hihi : ',router.query.id)
            if (router.query.id !== undefined) {
                axios(`http://localhost:8082/video/${router.query.id}`)
                    .then(res => {
                        const {status, data} = res;
                        setVideo(data);
                        console.log(video);
                    })
                    .catch(err => console.error(err));
            }
        }
    }, [router.query.id])
    return (
        <>
            <Navbar/>
            <VideoSingleContainer />
        </>
    )
}
