import React from 'react';
import {Theme, createStyles, makeStyles} from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import IconButton from "@material-ui/core/IconButton";
import {Avatar, Card, CardActions, CardContent, CardHeader, CardMedia, Collapse, Typography} from "@material-ui/core";
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import clsx from "clsx";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'space-around',
            backgroundColor: theme.palette.background.paper,
        },
        gridList: {
            flexWrap: 'wrap',
            // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
            transform: 'translateZ(0)',
        },
        media: {
            height: 0,
            paddingTop: '56.25%', // 16:9
        },
        expand: {
            transform: 'rotate(0deg)',
            marginLeft: 'auto',
            transition: theme.transitions.create('transform', {
                duration: theme.transitions.duration.shortest,
            }),
        },
        expandOpen: {
            transform: 'rotate(180deg)',
        },
        avatar: {
            backgroundColor: red[500],
        },
    }),
);

const tileData = [
    {
        id: 1,
        comment: "safsdfsdfdsfsdfsadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafdadfdasfsdfsfdsafd" +
