import React, {useState, useEffect} from 'react';
import {Theme, createStyles, makeStyles} from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import axios from "axios";
import {VideoResponseDto} from "../../api/video/VideoResponseDto";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'space-around',
            overflow: 'hidden',
            backgroundColor: theme.palette.background.paper,
        },
        gridList: {
            flexWrap: 'nowrap',
            // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
            transform: 'translateZ(0)',
        },
        title: {
            color: theme.palette.primary.contrastText,
        },
        titleBar: {
            background:
                'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
        },
    }),
);


export default function HotVideoGridListByGoodCnt() {
    const classes = useStyles();
    const [videos, setVideos] = useState([]);

    useEffect(() => {
        axios.get("http://127.0.0.1/api/video/order/goodCount",
            {
                headers: {
                    "Authorization": localStorage.getItem("token"),
                }
            }
        ).then(res => {
            setVideos(res.data)
        }).catch(err => console.log(err));
    }, []);

    return (
        <div className={classes.root}>
            <GridList className={classes.gridList} cols={4.5}>
                {videos.map((v) => (
                    <GridListTile key={v.id}>
                        <video width="100%" controls  src={v.videoUrl}/>
                        <GridListTileBar
                            title={v.title}
                            subtitle={v.description}
                            classes={{
                                root: classes.titleBar,
                                title: classes.title,
                            }}
                            actionIcon={
                                <IconButton aria-label={`star ${v.title}`}>
                                    <StarBorderIcon className={classes.title}/>
                                </IconButton>
                            }
                        />
                    </GridListTile>
                ))}
            </GridList>
        </div>
    );
}
