import React, {useEffect, useState} from 'react';
import {Theme, createStyles, makeStyles} from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import axios from "axios";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'space-around',
            overflow: 'hidden',
            backgroundColor: theme.palette.background.paper,
        },
        gridList: {
            flexWrap: 'wrap',
            // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
            transform: 'translateZ(0)',
        },
    }),
);


export default function DefaultVideoGridList({initVideos = []}) {
    const classes = useStyles();

    const [videos, setVideos] = useState(initVideos);

    useEffect(() => {
        console.log('token: ', localStorage.getItem("token"))

        const response = axios.get("http://127.0.0.1:8080/api/video/order/goodCount",
            {
                headers: {
                    Authorization: localStorage.getItem("token"),
                }
            }
        ).then(response => {
            console.log(response)
            setVideos(response.data)
        }).catch(err => console.log(err));
    }, []);

    return (
        <div className={classes.root}>
            <GridList cellHeight={160} className={classes.gridList} cols={4}>
                {videos.map((v) => (
                    <GridListTile key={v.id} cols={1}>
                        <img src={v.thumbnail} alt={v.title}/>
                    </GridListTile>
                ))}
            </GridList>
        </div>
    );
}

DefaultVideoGridList.getInitialProps = async (ctx) => {
    const videos: Promise<VideoResponseDto[]> = axios.get("http://127.0.0.1/api/video/order/topView",
        {
            headers: {
                "Authorization": localStorage.getItem("token"),
            }
        }
    ).then(res => {
        const {status, data} = res;
        console.log(`status : ${status}`)
        return data;
    }).catch(err => console.log(err));

    return { videos };
}
