import React, {useState, useEffect} from 'react';
import {Theme, createStyles, makeStyles} from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import axios from "axios";
import {NextPageContext} from "next";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'space-around',
            overflow: 'hidden',
            backgroundColor: theme.palette.background.paper,
        },
        gridList: {
            flexWrap: 'nowrap',
            // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
            transform: 'translateZ(0)',
        },
        title: {
            color: theme.palette.primary.contrastText,
        },
        titleBar: {
            background:
                'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
        },
    }),
);


export default function HotVideoGridListByGoodCnt({initVideos}) {
    const classes = useStyles();

    const [videos, setVideos] = useState(initVideos);


    useEffect(() => {
        axios.get("http://127.0.0.1:8080/api/video/order/topView",
            {
                headers: {
                    "Authorization": localStorage.getItem("token"),
                }
            }
        ).then(res => {
            const {status, data} = res;
            console.log(`status : ${status}`)
            console.log(`data : ${data}`)
            setVideos(data)
        }).catch(err => console.log(err));

        console.log('init: ', initVideos)
    })

    return (
        <div className={classes.root}>
            <GridList className={classes.gridList} cols={4.5}>
                {videos.map((v) => (
                    <GridListTile key={v.id}>
                        <img src={v.thumbnailUrl}/>
                        <GridListTileBar
                            title={v.title}
                            subtitle={v.description}
                            classes={{
                                root: classes.titleBar,
                                title: classes.title,
                            }}
                            actionIcon={
                                <IconButton aria-label={`star ${v.title}`}>
                                    <StarBorderIcon className={classes.title}/>
                                </IconButton>
                            }
                        />
                    </GridListTile>
                ))}
            </GridList>
        </div>
    );
}

HotVideoGridListByGoodCnt.getInitialProps = async (ctx: NextPageContext) => {

    const initVideos: Promise<VideoResponseDto[]> = axios.get("http://127.0.0.1:8080/api/video/order/topView",
        {
            headers: {
                "Authorization": localStorage.getItem("token"),
            }
        }
    ).then(res => {
        const {status, data} = res;
        console.log(`status : ${status}`)
        console.log(`data : ${data}`)
        return data;
    }).catch(err => console.log(err));

    console.log('init: ', initVideos)
    return {initVideos};
}
