import React, {useState} from "react";
import {Avatar, Typography} from "@material-ui/core";
import MoodBadOutlinedIcon from '@material-ui/icons/MoodBadOutlined';
import EmojiEmotionsOutlinedIcon from '@material-ui/icons/EmojiEmotionsOutlined';

export default function VideoMetadataContainer() {
    const [good, setGood] = useState(0);
    const [bad, setBad] = useState(0);
    const [goodStatus, setGoodStatus] = useState(false);
    const [badStatus, setBadStatus] = useState(false);
    const [subscribe, setSubscribe] = useState(false);


    async function goodIconClickHandler() {
        if (goodStatus === false) {
            setGood(good + 1);
        } else {
            setGood(good - 1);
        }
        setGoodStatus(!goodStatus);
    }

    async function badIconClickHandler() {
        if (badStatus === false) {
            setBad(bad+1);
        } else {
            setBad(bad - 1);
        }
        setBadStatus(!badStatus);
    }

    async function subscribeHandler() {
        setSubscribe(!subscribe);
    }

    return (
        <div className="video-bottom-section" style={{margin: '15px', padding: '10px'}}>
            <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg"/>

            <div className="video-details">
                <a href="#" className="video-title">Video Title</a>
                <span><a href={`/community/1`} className="video-channel-name">Channel Name</a>
                    &nbsp;&nbsp;&nbsp;
                    <button style={{
                        color: "white",
                        background: "#FF5335",
                        borderRadius: "15%"
                    }} onClick={subscribeHandler}>{subscribe ? "cancel" : "subscribe"}</button>
                </span>

                <div className="video-metadata">
                    <span>12K views</span>
                    •
                    <span>1 week ago</span>

                    <br/><br/>

                    <span>
                        <EmojiEmotionsOutlinedIcon onClick={goodIconClickHandler}/> <strong>Good: {good}</strong>
                        &nbsp;&nbsp;&nbsp;
                        <MoodBadOutlinedIcon onClick={badIconClickHandler}/> <strong>Bad: {bad}</strong>
                    </span>

                    <br/><br/>

                    <Typography
                        component="span"
                        variant="body2"
                        //className={classes.inline}
                        color="textPrimary">
                        safsdafasdfsdfsdafsadfsadfsdafsadfsafsd fasdfsdfsdafsadfsadfsdafsadfsafsdafasdfsdfsdafsadfsadfsdafsadfsadfsdafsadfdafsadf safsdafasdfsdfsdafsadfsadfsdafsadfsafsdafasdfsdfs dafsadfsadfsdafsadfsafsdafasdfsdfsdafsadfsadfsdafsadfsadfsdafsadfdafsadf
                        safsdafasdfsdfsdafsadfsadfsdafsadfsafsdafasdfsdfsdafsadfsadfsdafsadfsafsdafasdfsdfsdafsadfsadfsdafsadfsadfsdafsadfdafsadf safsdafasdfsdfsdafsadfsadfsdafsadfsafsdafasdfsdfsdafsadfsadfsdafsadfsafsdafasdfsdfsdafsadfsadfsdafsadfsadfsdafsadfdafsadf dsfasfdsfsdafsdfdsafsafsfsdfsafsa
                    </Typography>
                    <div className="video-description">

                    </div>
                </div>
            </div>
        </div>
    )
}
