import React from 'react';
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MailOutlineIcon from '@material-ui/icons/MailOutline';
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import {Card, CardContent, FormControl} from "@material-ui/core";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import CardActions from "@material-ui/core/CardActions";
import {useState} from "react";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root : {
            margin: theme.spacing(1),
        },
        signUpBox: {
            float: 'right',
            width: '30%',
            minWidth: '300px',
            height: '100%',
            overflow: 'hidden',
            textAlign: 'center',
            verticalAlign: 'center',
            border: '0.5px solid #556CD6',
            marginLeft: '100px'
        }
    }),
);

export default function SignUpBox() {
    const classes = useStyles();

    const [email, setMail] = useState("");
    const [name, setName] = useState("")
    const [pwd, setPwd] = useState("")

    const onEmailInputHandler = e => {
        e.preventDefault();
        setMail(e.target.value);
    }

    const onNameInputHandler = e => {
        e.preventDefault();
        setName(e.target.value);
    }

    const onPwdInputHandler = e => {
        e.preventDefault();
        setPwd(e.target.value);
    }

    interface signUpDto {
        email: string,
        name: string,
        pwd: string
    }

    const signUpSubmitHandler = async e => {
        e.preventDefault();

    }

    return (
        <Card className={classes.signUpBox}>
            <CardContent>
                <Typography variant="h5" component="h2">
                    Sign Up Today!
                </Typography>
                <FormControl className={classes.root}>
                    <Grid container spacing={1} alignItems="flex-end">
                        <Grid item>
                            <MailOutlineIcon />
                        </Grid>
                        <Grid item>
                            <TextField
                                id="input-with-icon-grid"
                                type="email"
                                label="Email"
                                onChange={onEmailInputHandler}
                            />
                        </Grid>
                    </Grid>
                    <Grid container spacing={1} alignItems="flex-end">
                        <Grid item>
                            <AccountCircle />
                        </Grid>
                        <Grid item>
                            <TextField
                                id="input-with-icon-grid"
                                label="Name"
                                onChange={onNameInputHandler}
                            />
                        </Grid>
                    </Grid>
                    <Grid container spacing={1} alignItems="flex-end">
                        <Grid item>
                            <VpnKeyIcon />
                        </Grid>
                        <Grid item>
                            <TextField
                                id="input-with-icon-grid"
                                type="password"
                                label="Password"
                                onChange={onPwdInputHandler}
                            />
                        </Grid>
                    </Grid>
                    <CardActions>
                        <Button  size="large"
                            onClick={signUpSubmitHandler}
                        >Sign Up For Heythere!</Button>
                    </CardActions>
                </FormControl>
            </CardContent>
        </Card>
    );
}
