export default function Upload() {
    return (
        <div className="jst-material-input-file">
            <div className="container mdl-shadow--6dp">
                <h2>Material Input File</h2>
                <h6>Inspired by
                    <a href="https://codepen.io/thirdtiu/pen/wBKKyp">
                        Avelino P. Tiu III
                    </a>
                </h6>
                <div
                    className="file-upload mdl-button mdl-js-button mdl-button--raised mdl-js-ripple-effect mdl-button--accent">
                    <span>BROWSE</span>
                    <input type="file" name="FileAttachment" id="FileAttachment" className="upload"/>
                </div>
                <div
                    className="file-upload mdl-button mdl-js-button mdl-button--raised mdl-js-ripple-effect mdl-button--accent">
                    <span>BROWSE</span>
                    <input type="file" name="FileAttachment" id="FileAttachment" className="upload"/>
                </div>
                <input type="text" id="fileuploadurl" readOnly placeholder="Maximum file size is 1GB" />
                <div>
                    <input type="text"/>
                    <textarea name="" id="" cols={30} rows={10} />
                </div>
            </div>


            <style>{`
body{
  background: #FF4081; }

a {
text-decoration: none; }

.jst-material-input-file {
  margin-top: 70px; }

.container{
  width: 400px;
  padding: 40px 20px;
  right: 0;
  left: 0;
  background: #fff;
  margin: auto; }

h2{
  margin-bottom: -30px;
  text-align: center;}

h6{
  margin-bottom: 30px;
  color: gray;
  text-align: center;
  padding: 10px; }

.file-upload {
\tmargin: 0 10px 0 25px;
}
.file-upload input.upload {
\tposition: absolute;
\ttop: 0;
\tright: 0;
\tmargin: 0;
\tpadding: 0;
  z-index: 10;
\tfont-size: 20px;
\tcursor: pointer;
\theight: 36px;
\topacity: 0;
\tfilter: alpha(opacity=0); }

#fileuploadurl{
  border: none;
  font-size: 12px;
  padding-left: 0;
  width: 250px; }
            `}</style>

        </div>
    )
}
