import React,{useState} from "react";
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import axios from "axios";
import {Button} from "@material-ui/core";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
        },
        textField: {
            marginLeft: theme.spacing(1),
            marginRight: theme.spacing(1),
            minWidth: '300px',
            maxWidth: '1000px',
            rows: 19
        },
    }),
);


interface Metadata {
    preview: string;
    raw?: File;
}

export default function UploadVideo() {
    const [title, setTitle] = useState<string>("");
    const [description, setDescription] = useState<string>("");
    const [thumbnail, setThumbnail] = useState<Metadata>({
        preview: "",
        raw: null,
    });
    const [video, setVideo] = useState<Metadata>({
       preview: "",
       raw: null,
    });

    const classes = useStyles();

    async function onSubmitHandler() {
        const form = new FormData();
        form.append("requestUserId", "1");
        form.append("title", title);
        form.append("description", description);
        form.append("thumbnail", thumbnail.raw);
        form.append("video", video.raw);

        await axios.post("http://localhost:8082/registration", form)
            .then(response => {
                const {status, data} = response;
                if (status === 201 || status == 200)
                    console.log(data);
            })
            .catch(error => {
                console.error(error);
            })
    }

    return (
        <div className={classes.root}>
            <div>
                <TextField
                    className={classes.textField}
                    label="Title"
                    style={{ margin: 8 }}
                    placeholder="title..."
                    fullWidth
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="outlined"
                    onChange={e => setTitle(e.target.value)}
                />
                <TextField
                    className={classes.textField}
                    label="Video Description"
                    style={{ margin: 8 }}
                    placeholder="video description"
                    fullWidth
                    multiline
                    rows={10}
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="outlined"
                    onChange={e => setDescription(e.target.value)}
                />
                <label htmlFor="upload-thumbnail">
                    <input
                        style={{ display: 'none' }}
                        id="upload-thumbnail"
                        name="upload-photo"
                        type="file"
                        onChange={e => setThumbnail({
                            preview: URL.createObjectURL(e.target.files[0]),
                            raw: e.target.files[0]
                        })}
                    />

                    <Button color="primary" variant="contained" component="span">
                        Upload Thumbnail
                    </Button>
                </label>
                <br/>
                {thumbnail.raw !== null &&
                    <img src={thumbnail.preview} alt="thumbnail"/>
                }

                <br/>
                <label htmlFor="upload-video">
                    <input
                        style={{ display: 'none' }}
                        id="upload-video"
                        name="upload-video"
                        type="file"
                        onChange={e => setVideo({
                                preview: URL.createObjectURL(e.target.files[0]),
                                raw: e.target.files[0],
                            })}
                    />
                    {video.raw !== null &&
                        <video autoPlay controls src={thumbnail.preview} alt="video"/>
                    }

                    <Button color="primary" variant="contained" component="span">
                        Upload Video
                    </Button>
                </label>

                <br/>
                <Button color="primary" variant="contained" component="span">
                    Upload
                </Button>

            </div>
        </div>
    );
}
