        createdAt: new Date(),
        modifiedAt: new Date(),
        thumbnailUrl: "string",
        videoUrl: "http://techslides.com/demos/sample-videos/small.mp4",
        viewCount: 15,
        userId: 1,
        email: "string",
        name: "string",
        userImg: "string"
    },
]

export default function DefaultVideoGridList({initVideos = []}) {
    const classes = useStyles();

    const [videos, setVideos] = useState(initVideos);

    useEffect(() => {
        console.log('token: ', localStorage.getItem("token"))

        axios.get("http://127.0.0.1:8080/api/video/video",
            {
                headers: {
                    Authorization: localStorage.getItem("token"),
                }
            }
        ).then(response => {
            console.log(response)
            setVideos(response.data)
        }).catch(err => console.log(err));
    }, []);

    return (
        <div className={classes.root}>
            <GridList cellHeight={160} className={classes.gridList} cols={4}>
                {videos.map((v) => (
                    <GridListTile key={v.id} cols={1}>
                        <img src={v.thumbnailUrl} alt={v.title}/>
                    </GridListTile>
                ))}
            </GridList>
        </div>
    );
}

DefaultVideoGridList.getInitialProps = async (ctx) => {
    const videos: Promise<VideoResponseDto[]> = axios.get("http://127.0.0.1/api/video/video",
        {
            headers: {
                "Authorization": localStorage.getItem("token"),
            }
        }
    ).then(res => {
        const {status, data} = res;
        console.log(`status : ${status}`)
        return data;
    }).catch(err => console.log(err));

    return { videos };
}
