import Navbar from "../../components/navbar";
import VideoSingleContainer from "../../components/video/video-single-container";
import {useRouter} from "next/router";
import {useEffect, useState} from "react";
import axios from "axios";
import {NextPageContext} from "next";
import {parseCookies} from "../../cookie/parseCookies";


interface LargeComment {
    id: number;
    comment: string;
    createdAt: string;
    modifiedAt: string;
    userId: number;
    email: string;
    name: string;
    userImg: string;
}

interface Comment {
    id: number;
    comment: string;
    createdAt: string;
    modifiedAt: string;
    userId: number;
    email: string;
    name: string;
    userImg: string;
    largeComments: LargeComment[];
}

interface Video {
    id: number;
    title: string;
    description: string;
    createdAt: string;
    modifiedAt: string;
    thumbnailUrl: string;
    videoUrl: string;
    viewCount: number
    goodStatus: boolean,
    badStatus: boolean;
    userId: number;
    email: string;
    name: string;
    userImg: string;
    comments: Comment[];
}

export default function Video() {
    const [video, setVideo] = useState<Video>(data);
    const router = useRouter()
    const [id, setId] = useState(0);

    useEffect(() => {
        if (!sessionStorage.getItem("token")) {
            window.alert("로그인페이지로 이동합니다");
            window.location.href = "/login";
        }

        async function loadData() {
            console.log('hihi : ', router.query.id)
            if (router.query.id !== undefined) {
                setId(Number(localStorage.getItem("id")));
                console.log(`http://localhost:8082/video/${router.query.id}`)
                const response = await fetch(`http://localhost:8082/video/${router.query.id}?requestUserId=${id}`);
                const data:Video = await response.json();
                console.log('data: ', data);
                await setVideo(data);
                console.log('video : ',video)
            }
        }

        console.log('data : ',data);
        if (!data) {
            loadData();
        }
    }, [router.query.id]);


    return (
        <>
            <Navbar/>
            <VideoSingleContainer data={data}/>
        </>
    )
}

