import React, {useState} from 'react';
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';
import VpnKeyIcon from "@material-ui/icons/VpnKey";
import Button from "@material-ui/core/Button";
import CardActions from "@material-ui/core/CardActions";
import axios from "axios"

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root : {
            margin: theme.spacing(1),
        },
    }),
);

export default function LoginBox() {
    const classes = useStyles();
    const [email, setMail] = useState("");
    const [password, setPwd] = useState("")


    const onEmailInputHandler = e => {
        e.preventDefault();
        setMail(e.target.value);
    }

    const onPwdInputHandler = e => {
        e.preventDefault();
        setPwd(e.target.value);
    }

    interface LoginDto {
        email: string,
        password: string
    }

    interface LoginResponse {
        accessToken: string,
        tokenType: string
    }

    const loginSubmitHandler = async e => {
        e.preventDefault();
        const data: LoginDto = {
            email,
            password
        };

        const response = axios.post("http://localhost:8080/auth/login", data, null);
        const responseData: LoginResponse = (await response).data;
        const {accessToken, tokenType} = responseData;

        localStorage.setItem("token", `${tokenType} ${accessToken}`);
        console.log(localStorage.getItem("token"))
    }


    return (
        <div>
            <div className={classes.root}>
                <Grid container spacing={1} alignItems="flex-end">
                    <Grid item>
                        <AccountCircle />
                    </Grid>
                    <Grid item>
                        <TextField
                            id="input-with-icon-grid"
                            type="email"
                            label="Email"
                            onChange={onEmailInputHandler}
                        />
                    </Grid>
                </Grid>
                <Grid container spacing={1} alignItems="flex-end">
                    <Grid item>
                        <VpnKeyIcon />
                    </Grid>
                    <Grid item>
                        <TextField
                            id="input-with-icon-grid"
                            type="password"
                            label="Password"
                            onChange={onPwdInputHandler}
                        />
                    </Grid>
                </Grid>
                <CardActions>
                    <Button  size="large" onClick={loginSubmitHandler}>Login Now!</Button>
                </CardActions>
            </div>
        </div>
    );
}
