import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import {useTheme} from '@material-ui/core/styles';
import {TextField} from "@material-ui/core";
import axios from "axios";

export default function VideoUploadModal() {
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [thumbnail, setThumbnail] = useState({preview: "", raw: ""});
    const [video, setVideo] = useState({preview: "", raw: ""});

    async function onSubmitHandler () {
        const form = new FormData();
        form.append("title", title);
        form.append("description", description);
        form.append("thumbnail", thumbnail.raw);
        form.append("video", video.raw);

        const response = await axios.post("http://localhost:8082/registration");
        const { status, data } = response;
        if (status === 201) {
            console.log(data);
        }

    }

    function thumbnailFileChangeHandler(e) {
        console.log(`thumbnail : ${e.target.files[0]}`);
        setThumbnail({
            preview: URL.createObjectURL(e.target.files[0]),
            raw: e.target.files[0],
        });
    }

    function videoFileChangeHandler(e) {
        console.log(`video : ${e.target.files[0]}`);

        setVideo({
            preview: URL.createObjectURL(e.target.files[0]),
            raw: e.target.files[0],
        });
    }

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <img src="/icons/video-plus.svg" onClick={handleClickOpen}/>

            <Dialog
                fullScreen={fullScreen}
                open={open}
                onClose={handleClose}
                aria-labelledby="responsive-dialog-title"
            >
                <DialogTitle id="responsive-dialog-title">{"Video Upload"}</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        <form action="">
                            <TextField  label="video title" onChange={e => setTitle(e.target.value)}/>
                            <br/>
                            <br/>
                            <TextField  label="video description" onChange={e => setDescription(e.target.value)} />
                            <br/>
                            <input type="file" />
                            <input type="file"/>

                            <br/>

                        </form>
                    </DialogContentText>
                </DialogContent>

                <img src={thumbnail.preview} alt=""/>
                <img src={video.preview} alt=""/>
                <DialogActions>
                    <Button autoFocus onClick={handleClose} color="primary">
                        Disagree
                    </Button>
                    <Button onClick={onSubmitHandler} color="primary" autoFocus>
                        Agree
                    </Button>
                </DialogActions>
            </Dialog>

        </div>
    );
}
