import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import {useTheme} from '@material-ui/core/styles';

export default function VideoUploadModal() {
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <Button onClick={handleClickOpen}>
                <img src="/icons/video-plus.svg"/>
            </Button>

            <Dialog
                fullScreen={fullScreen}
                open={open}
                onClose={handleClose}
                aria-labelledby="responsive-dialog-title"
            >
                <DialogTitle id="responsive-dialog-title">{"Video Upload"}</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                      <div className="container">
                        <div className="row">
                          <div className="col-12 col-md-6">
                            <div className="p-5">
                              <h4>
                                <i className="fa fa-file-word-o"></i>
                                Add New Post
                              </h4>
                              <hr />
                                <form action="store.php" method="post" id="form" encType="multipart/form-data">
                                  <div className="form-group">
                                    <label htmlFor="title" className="text-primary text-uppercase">Post title</label>
                                    <input type="text" className="form-control" id="title" name="title" />
                                  </div>
                                  <div className="form-group">
                                    <label htmlFor="description" className="text-primary text-uppercase">Post
                                      Description</label>
                                    <textarea className="form-control" id="description" name="description"
                                              rows={5} />
                                  </div>
                                  <div className="form-group ">
                                    <label className="text-primary text-uppercase">Upload Photo</label>
                                    <div className="custom-uploader border border-faded rounded p-2">
                                      <input type="file" id="photo" className="d-none" accept="image/jpeg,image/png"
                                             name="photo[]" multiple />
                                        <div className="uploader d-flex">
                                          <div className="uploader-btn d-flex justify-content-center align-items-center border border-faded rounded">
                                            <i className="fa fa-photo text-secondary fa-1x"></i>
                                          </div>
                                          <div className="photo-show flex-grow-1"></div>
                                        </div>
                                    </div>
                                  </div>
                                  <button className="btn btn-primary text-uppercase upload">
                                    <i className="fa fa-upload"></i> Upload
                                  </button>
                                </form>
                            </div>
                          </div>
                        </div>
                      </div>
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button autoFocus onClick={handleClose} color="primary">
                        Disagree
                    </Button>
                    <Button onClick={handleClose} color="primary" autoFocus>
                        Agree
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}
