import {createStyles, makeStyles, Theme} from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import ListItemText from "@material-ui/core/ListItemText";
import React,{useState} from "react";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import {Button, TextField} from "@material-ui/core";
import {useRouter} from "next/router";
import LargeCommentList from "../comment/large-comment-container";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            marginLeft: '50px',
            width: '100%',
            backgroundColor: theme.palette.background.paper,
        },
        inline: {
            display: 'inline',
        },
    }),
);


interface LargeComment {
    id: number;
    comment: string;
    createdAt: string;
    modifiedAt: string;
    userId: number;
    email:string;
    name: string;
    userImg: string;
}


interface LargeCommentList {
    largeComments: LargeComment[];
}

export default function VideoLargeCommentContainer({largeComments}:LargeCommentList) {
    const {query} = useRouter();
    const [largeCommentList, setLargeCommentList] = useState(largeComments);
    const classes = useStyles();
    console.log('largecomment :', largeComments);
    console.log('largecomment : ', largeCommentList)

    return (
        <>
            <form>
                <TextField style={{width: '100%'}} label="Leave Your Comment Here..!"/>
                <br/>
                <Button variant="outlined" color="primary">
                    Submit
                </Button>
                <Button type="reset" variant="outlined">Reset</Button>
            </form>

            <List className={classes.root}>
                {largeComments.length > 0 && largeCommentList.map((c,index) => (
                    <>
                        <ListItem key={c.id} alignItems="flex-start">
                            <ListItemAvatar>
                                <a href={`/community/${c.userId}`}>
                                    <Avatar alt={c.name} src={c.userImg}/>
                                </a>
                            </ListItemAvatar>
                            <ListItemText
                                primary={c.name}
                                secondary={
                                    <React.Fragment>
                                        <Typography
                                            component="span"
                                            variant="body2"
                                            className={classes.inline}
                                            color="textPrimary"
                                        >
                                        </Typography>
                                        {c.comment}
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                        <VideoLargeCommentContainer largeComments={c.largeComments}/>
                        <Divider variant="inset" component="li"/>
                        <br/><br/>
                    </>
                ))}
            </List>
        </>
    )
}
