import React, {useState} from "react";
import {createStyles, makeStyles, Theme} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import axios from "axios";
import {Button} from "@material-ui/core";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
        },
        textField: {
            marginLeft: theme.spacing(1),
            marginRight: theme.spacing(1),
            minWidth: '300px',
            maxWidth: '1000px',
            rows: 19
        },
        preview: {
            minWidth: '300px',
            maxWidth: '1000px'
        },
        button: {
            margin: '10px',
            padding: '10px'
        }
    }),
);


interface Metadata {
    preview: string;
    raw?: File;
}

export default function UploadVideo() {
    const [title, setTitle] = useState<string>("");
    const [content, setContent] = useState<string>("");
    const [picture, setPicture] = useState<Metadata>({
        preview: "",
        raw: null
    });

    const classes = useStyles();

    async function onSubmitHandler() {
        const form = new FormData();
        form.append("requestUserId", "1");
        form.append("title", title);
        form.append("content", content);
        form.append("img", picture.raw);

        await axios.post("http://localhost:8081/v1/registration/post", form)
            .then(response => {
                const {status, data} = response;
                if (status === 201 || status == 200)
                    console.log(data);
            })
            .catch(error => {
                console.error(error);
            })
    }

    return (
        <div className={classes.root}>
            <div>
                <TextField
                    className={classes.textField}
                    label="Title"
                    style={{margin: 8}}
                    placeholder="title..."
                    fullWidth
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="outlined"
                    onChange={e => setTitle(e.target.value)}
                />

                <TextField
                    className={classes.textField}
                    label="Content"
                    style={{margin: 8}}
                    placeholder="Posting..."
                    fullWidth
                    multiline
                    rows={10}
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="outlined"
                    onChange={e => setContent(e.target.value)}
                />

                <label htmlFor="upload-picture">
                    <input
                        style={{display: 'none'}}
                        id="upload-thumbnail"
                        name="upload-photo"
                        type="file"
                        onChange={e => setPicture({
                            preview: URL.createObjectURL(e.target.files[0]),
                            raw: e.target.files[0]
                        })}
                    />

                    <Button className={classes.button} color="primary" variant="contained" component="span">
                        Upload Thumbnail
                    </Button>
                </label>

                {picture.raw !== null &&
                (
                    <>
                        <br/>
                        <Button className={classes.button} color="secondary" variant="contained" component="span"
                                onClick={() => setPicture({preview: "", raw: null})}>
                            Remove
                        </Button>
                        <img className={classes.preview} src={picture.preview} />
                        <br/>
                    </>
                )

                }
                <br/>


                <br/>
                <br/>
                <Button className={classes.button} color="primary" variant="contained" component="span"
                        onClick={onSubmitHandler}>
                    Upload
                </Button>
            </div>
        </div>
    );
}
